mapper.insert:
insert
    into member
(
      emp_id
    , emp_pwd
    , emp_no
    , emp_name
    , dept_code
    , job_code
    , hire_date
    , emp_resinum
    , emp_phone
    , emp_email
)
values
(
      #{ emp_id }
    , #{ emp_pwd }
    , #{ emp_no }
    , #{ emp_name }
    , #{ dept_code }
    , #{ job_code }
    , #{ hire_date }
    , #{ emp_resinum }
    , #{emp_phone}
    , #{emp_email}
)

mapper.update:
    update member
set emp_name = #{emp_name}
, emp_no = #{emp_no}
, emp_id = #{emp_id}
, emp_resinum = #{emp_resinum}
, dept_code = #{dept_code}
, job_code = job_code
, salary = salary
, modify_date = sysdate
where emp_id = #{emp_id}
